    /***
     * Decodes a string from UBFunkeys RDF format
     * @param encoded string to decode
     * @return decoded string
     */
    public static String decode(String encoded) {
        int currentCol = 0;
        final char[] arr = encoded.toCharArray();
        char[] ret = new char[arr.length];
        int pos = 0;
        int endchar = 0;
        for (final char character : arr) {
            for (int i = 0; i < 256; i++) {
                if (RDFUtil.col[currentCol][i] == character) {
                    ret[pos++] = RDFUtil.code[i];
                    if (RDFUtil.code[i] == 0x3E) {
                        endchar = pos;
                    }
                    break;
                }
            }
            currentCol++;
            if (currentCol > 7) {
                currentCol = 0;
            }
        }

        return new String(ret).substring(RDFUtil.start.length, endchar);

    }

    /***
     * Encodes a string to UBFunkeys RDF format
     * @param decoded string to encode
     * @return encoded string
     */
    public static String encode(String decoded) {
        int currentCol = 0;
        final char[] arr = decoded.toCharArray();
        char[] ret = new char[arr.length];
        int pos = 0;
        for (char character : arr) {
            for (int i = 0; i < 256; i++) {
                if (RDFUtil.code[i] == character) {
                    ret[pos++] = RDFUtil.col[currentCol][i];
                    break;
                }
            }
            currentCol++;
            if (currentCol > 7) {
                currentCol = 0;
            }
        }
        return new String((new String(RDFUtil.start) + new String(ret) + new String(RDFUtil.end[currentCol])).getBytes(StandardCharsets.ISO_8859_1), StandardCharsets.ISO_8859_1);
    }

